mapper.select:
        SELECT count(1) 
        FROM postback_tune 
		WHERE 1=1
		AND regtime >= #{startdate}
		AND regtime <= #{enddate}
		AND campaign = #{campaign}
		AND channel = #{channel}
		AND secondary_publisher = #{secondary_publisher}

		AND appkey = #{searchkeyword}
        AND package_name = #{searchkeyword}
        AND gaid = #{searchkeyword}
        AND cb_param3 like CONCAT('%',#{searchkeyword},'%')
        AND event_type_code = #{searchkeyword}

        SELECT seq, regtime, aaid, idfa, agency, secondary_publisher, campaign, campaign_id, channel, ad_set_name, ad_set_id, ad_name, ad_id, 
        	click_id, event_name, event_timestamp, is_attributed, bundle_id, app_name, app_version, language, os, os_version, client_ip, country, 
        	device_id, goal_id, user_agent
        FROM postback_tune
        WHERE 1=1
		AND regtime >= #{startdate}
		AND regtime <= #{enddate}
		AND campaign = #{campaign}
		AND channel = #{channel}
		AND secondary_publisher = #{secondary_publisher}
		AND appkey = #{searchkeyword}
        AND package_name = #{searchkeyword}
        AND gaid = #{searchkeyword}
        AND cb_param3 like CONCAT('%',#{searchkeyword},'%')
        AND event_type_code = #{searchkeyword}
        ORDER BY seq DESC

        LIMIT #{count} offset #{start}

mapper.update:
UPDATE postback_tune SET 
	appkey = #{appkey}
WHERE seq = #{seq}
