mapper.select:
        SELECT count(1) 
        FROM click_log 
		WHERE 1=1
		AND camp_no = #{camp_no}
		AND pub_id = #{pub_id}
		AND sub_pub_id = #{sub_pub_id}
		AND rtime >= #{startdate}
		AND rtime <= #{enddate}
		AND camp_id like CONCAT('%',#{searchkeyword},'%')
        AND camp_no = #{searchkeyword}
        AND pub_id = #{searchkeyword}
        AND sub_pub_id = #{searchkeyword}
        AND click_id = #{searchkeyword}
        AND idfa = #{searchkeyword}
        AND adid = #{searchkeyword}

        SELECT seq, camp_id, camp_no, click_id, pub_id, sub_pub_id, sub_param1, sub_param2, sub_param3, sub_param4, idfa, adid, rtime, ip
        FROM click_log
        WHERE 1=1
		AND camp_no = #{camp_no}
		AND pub_id = #{pub_id}
		AND sub_pub_id = #{sub_pub_id}
		AND rtime >= #{startdate}
		AND rtime <= #{enddate}
		AND camp_id like CONCAT('%',#{searchkeyword},'%')
        AND camp_no = #{searchkeyword}
        AND pub_id = #{searchkeyword}
        AND sub_pub_id = #{searchkeyword}
        AND click_id = #{searchkeyword}
        AND idfa = #{searchkeyword}
        AND adid = #{searchkeyword}
        ORDER BY seq DESC

        LIMIT #{count} offset #{start}

mapper.update:
UPDATE click_log SET 
	ip = #{ip}
WHERE seq = #{seq}
